var exports = module.exports = {};
/*
This module assigns colour to usernames in a chat server if they apear at the front of the message before the first :
*/
//1 Colour for each letter
var colours = {"a":"#0000FF","b":"#006600","c":"#330033","d":"#990000","e":"#990099","f":"#99FF00","g":"#CC00CC","h":"#FF00FF","i":"#CC0000","j":"#6600FF","k":"66FF99",
"l":"#33CCCC","m":"#00FF99","n":"#333CC","o":"#333FF","p":"#666666","q":"#6666FF",
"r":"#CC0033","s":"#FF66FF","t":"#CCFF33","u":"#6666FF","v":"#33FF66","w":"#660000","x":"#006666","y":"#00FF00","z":"#330066"}
exports.parseText = function(t){
	var u = "";
	var x= -1;
	var y= -1;
	//If allows for HTML tags (Black font to any usernames not starting with something > 'a' in the ascii table) 
	if(t.toString().substring(0,1).toLowerCase()>='a'){
		u = "<b>" +"<font color="+colours[t.toString().substring(0,1).toLowerCase()]+">" +t.toString().substring(0,t.indexOf(":")) + "</font></b>" + t.
		toString().substring(t.indexOf(":"),t.length);
		return u;
	}
	return t;
}
